import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const EditPage = () => {
    const [data, setData] = useState([]);
    const [editedData, setEditedData] = useState([]);
    const history = useHistory();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:5000/get_all_pipeline_data');
                setData(response.data.dataset || []);
                setEditedData(response.data.dataset || []);
            } catch (err) {
                console.error('Error fetching pipeline data:', err);
            }
        };
        fetchData();
    }, []);

    const handleInputChange = (e, rowIndex, column) => {
        const newData = [...editedData];
        newData[rowIndex][column] = e.target.value;
        setEditedData(newData);
    };

    const saveData = async () => {
        try {
            await axios.post('http://127.0.0.1:5000/update_pipeline_data', { data: editedData });
            alert('Data saved successfully!');
            history.push('/'); // Navigate back to the main page
        } catch (err) {
            console.error('Error saving data:', err);
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Edit Pipeline Data</h2>
            <table>
                <thead>
                    <tr>
                        <th>Asset</th>
                        <th>Latitude</th>
                        <th>Longitude</th>
                        <th>Operational Status</th>
                        <th>Speed</th>
                        <th>Heading</th>
                    </tr>
                </thead>
                <tbody>
                    {editedData.map((row, index) => (
                        <tr key={index}>
                            <td>{row.Asset}</td>
                            <td>
                                <input
                                    type="text"
                                    value={row.Latitude}
                                    onChange={(e) => handleInputChange(e, index, 'Latitude')}
                                />
                            </td>
                            <td>
                                <input
                                    type="text"
                                    value={row.Longitude}
                                    onChange={(e) => handleInputChange(e, index, 'Longitude')}
                                />
                            </td>
                            <td>
                                <input
                                    type="text"
                                    value={row.OperationalStatus}
                                    onChange={(e) => handleInputChange(e, index, 'OperationalStatus')}
                                />
                            </td>
                            <td>
                                <input
                                    type="text"
                                    value={row.Speed}
                                    onChange={(e) => handleInputChange(e, index, 'Speed')}
                                />
                            </td>
                            <td>
                                <input
                                    type="text"
                                    value={row.Heading}
                                    onChange={(e) => handleInputChange(e, index, 'Heading')}
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={saveData}>Save Changes</button>
        </div>
    );
};

export default EditPage;
