import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import 'leaflet/dist/leaflet.css';

const AssetTracker = () => {
    const [dataset, setDataset] = useState([]);
    const [step, setStep] = useState(0);
    const [modifiedDataset, setModifiedDataset] = useState([]);

    // Fetch the current dataset and step
    useEffect(() => {
        const fetchDataset = async () => {
            try {
                const response = await axios.get('http://localhost:5000/get_dataset');
                setDataset(response.data.dataset);
                setStep(response.data.step);
            } catch (error) {
                console.error('Error fetching dataset:', error);
            }
        };
        fetchDataset();
    }, []);

    // Process the next step
    const processStep = async () => {
        try {
            const response = await axios.post('http://localhost:5000/process_step');
            setDataset(response.data.dataset);
            setStep(response.data.step);
        } catch (error) {
            console.error('Error processing step:', error);
        }
    };

    // Update the dataset with user modifications
    const updateDataset = async () => {
        try {
            const response = await axios.post('http://localhost:5000/update_dataset', modifiedDataset);
            console.log(response.data.message);
        } catch (error) {
            console.error('Error updating dataset:', error);
        }
    };

    return (
        <div>
            <h2>Asset Tracker</h2>
            <p>Current Step: {step}</p>
            <button onClick={processStep}>Process Next Step</button>
            <button onClick={updateDataset}>Update Dataset</button>

            {/* Map */}
            <MapContainer center={[40.7128, -74.0060]} zoom={5} style={{ height: '400px', width: '100%' }}>
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution="&copy; OpenStreetMap contributors"
                />
                {dataset.map((asset, idx) => (
                    <Marker key={idx} position={[asset.Latitude, asset.Longitude]}>
                        <Popup>
                            {asset.Asset}: Speed {asset.Speed} knots, Heading {asset.Heading}Â°
                        </Popup>
                    </Marker>
                ))}
            </MapContainer>

            {/* Chart */}
            <h3>Speed Chart</h3>
            <LineChart
                width={600}
                height={300}
                data={dataset}
                margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
            >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="Asset" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="Speed" stroke="#8884d8" activeDot={{ r: 8 }} />
            </LineChart>
        </div>
    );
};

export default AssetTracker;
