import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

const AssetOverview = () => {
    const [assets, setAssets] = useState([]);
    const [error, setError] = useState(null);

    const fetchFinalDataset = async () => {
        try {
            const response = await axios.get('http://127.0.0.1:5000/get_pipeline_stage');
            if (response.data.step < 2) {
                setError('Pipeline not complete. Complete all stages to view assets.');
                setAssets([]);
            } else {
                setAssets(response.data.dataset || []);
            }
        } catch (err) {
            setError('Error fetching final dataset.');
            console.error(err);
        }
    };

    useEffect(() => {
        fetchFinalDataset();
    }, []);

    return (
        <div style={{ padding: '20px' }}>
            <h2>Asset Overview</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <div style={{ width: '100%', height: '500px', marginBottom: '20px' }}>
                <MapContainer center={[37.7749, -122.4194]} zoom={4} style={{ height: '100%', width: '100%' }}>
                    {/* Use a globe-like imagery layer */}
                    <TileLayer
                        url="https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}"
                        attribution="&copy; <a href='https://www.esri.com/en-us/arcgis/products/arcgis-online/overview'>Esri</a>"
                    />
                    {assets.map((asset, index) => (
                        <Marker
                            key={index}
                            position={[asset.Latitude, asset.Longitude]}
                        >
                            <Popup>
                                <b>{asset.Asset}</b>
                                <br />
                                Status: {asset.OperationalStatus}
                            </Popup>
                        </Marker>
                    ))}
                </MapContainer>
            </div>
        </div>
    );
};

export default AssetOverview;
