import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

const AssetOverview = () => {
    const [assets, setAssets] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchFinalDataset = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:5000/get_assets');
                setAssets(response.data.assets || []);
            } catch (err) {
                setError('Error fetching final dataset.');
                console.error(err);
            }
        };

        fetchFinalDataset();
    }, []);

    return (
        <div style={{ padding: '20px' }}>
            <h2>Asset Overview</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <div style={{ width: '80%', height: '400px', marginBottom: '20px' }}>
                <MapContainer center={[37.7749, -122.4194]} zoom={4} style={{ height: '100%', width: '100%' }}>
                    <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution="&copy; OpenStreetMap contributors"
                    />
                    {assets.map((asset, index) => (
                        <Marker
                            key={index}
                            position={[asset.Latitude, asset.Longitude]}
                        >
                            <Popup>
                                <b>{asset.Asset}</b>
                                <br />
                                Status: {asset.OperationalStatus}
                            </Popup>
                        </Marker>
                    ))}
                </MapContainer>
            </div>
        </div>
    );
};

export default AssetOverview;
