import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';

const AssetOverview = ({ dataset }) => {
    return (
        <div>
            <h2>Asset Overview</h2>

            {/* Map */}
            <MapContainer center={[40.7128, -74.0060]} zoom={5} style={{ height: '400px', width: '100%' }}>
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution="&copy; OpenStreetMap contributors"
                />
                {dataset.map((asset, idx) => (
                    <Marker key={idx} position={[asset.Latitude, asset.Longitude]}>
                        <Popup>
                            {asset.Asset}: {asset.OperationalStatus}
                        </Popup>
                    </Marker>
                ))}
            </MapContainer>

            {/* Chart */}
            <h3>Status Chart</h3>
            <LineChart
                width={600}
                height={300}
                data={dataset}
                margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
            >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="Asset" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="Speed" stroke="#8884d8" activeDot={{ r: 8 }} />
            </LineChart>
        </div>
    );
};

export default AssetOverview;
